import './index.css';
import React, { Component } from 'react';
import './App.css';
import CountryCombo from './AddressComponent/CountryCombo.js';
import StateCombo from './AddressComponent/StateCombo.js';
import CityCombo from './AddressComponent/CityCombo.js';

class App extends Component {
  constructor(props) {
    super(props);

    this.countries = '';
    this.states = '';
    this.cities = '';

    this.state = {
      selectedCountry: '',
      selectedState:'',
      selectedCity:''
    };

    this.callApi = this.callApi.bind(this);
    this.handleCountrySelectionChange = this.handleCountrySelectionChange.bind(this);
    this.handleStateSelectionChange = this.handleStateSelectionChange.bind(this);
    this.handleCitySelectionChange = this.handleCitySelectionChange.bind(this);
  }

  handleCountrySelectionChange(value) {
    this.setState({selectedCountry : value});
  }

  handleStateSelectionChange(value) {
    this.setState({selectedState : value});
  }

  handleCitySelectionChange(value) {
    this.setState({selectedCity : value});
  }

  componentDidMount() {
    this.callApi();
  }

  callApi(requestFor, selectedCountry, selectedState) {
    requestFor = requestFor || '';
    if (this.countries === '' && requestFor === '' ) {
      fetch('/api/country')
        .then(
          response => response.json()
          .then(
            parsedResponse => this.countries = parsedResponse)
        )
        .catch(err => console.log(err));
    } else if (requestFor === 'state') {
      let url = '/api/' + this.state.selectedCountry + '/state';
      fetch(url).then(response => response.json().then(parsedResponse => this.states = parsedResponse)).catch(err => console.log(err));
    } else if (requestFor === 'city') {
      let url = '/api/' + this.state.selectedCountry + '/' + this.state.selectedState + '/city';
      fetch(url).then(response => response.json().then(parsedResponse => this.cities = parsedResponse )).catch(err => console.log(err));
    }
  };

  render() {
    return (
      <div className="main-container">
        <CountryCombo countryDetails = {this.countries} callApi = {this.callApi} handleCountrySelectionChange = {this.handleCountrySelectionChange}/>
        <StateCombo stateDetails = {this.states} callApi = {this.callApi} handleStateSelectionChange = {this.handleStateSelectionChange}/>
        <CityCombo cityDetails = {this.cities} handleCitySelectionChange = {this.handleCitySelectionChange}/>
      </div>
    );
  }
}

export default App;
