import React, { Component } from 'react';
import './App.css';

class ItemList extends Component {
  render() {
    const liValue = Object.keys(this.props.countryDetails).map(key => <option>{this.props.countryDetails[key]}</option>);
    return (<select>{liValue}</select>);
  }
}

class App extends Component {
  state = {
    countries: '',
    states:'',
    cities:''
  };

  componentDidMount() {
    this.callApi();
  }

  callApi(requestFor) {
    requestFor = requestFor || '';
    if (this.state.countries === '' && requestFor === '' ) {
      fetch('/api/countries').then(response => response.json().then(parsedResponse => this.setState({ countries: parsedResponse })).catch(err => console.log(err)));
    } else if (requestFor === 'state') {
      fetch('/api/state').then(response => response.json().then(parsedResponse => this.setState({ states: parsedResponse })).catch(err => console.log(err)));
    } else if (requestFor === 'city') {
      fetch('/api/city').then(response => response.json().then(parsedResponse => this.setState({ cities: parsedResponse })).catch(err => console.log(err)));
    }
  };

  render() {
    return (
      <div>
        <ItemList countryDetails = {this.state.countries}/>
      </div>
    );
  }
}

export default App;

