import './index.css';
import React, { Component } from 'react';
import './App.css';
import CountryCombo from './AddressComponent/CountryCombo.js';
import StateCombo from './AddressComponent/StateCombo.js';
import CityCombo from './AddressComponent/CityCombo.js';

class App extends Component {
  constructor() {
    super();

    this.countries = '';
    this.states = '';
    this.cities = '';

    this.state = {
      selectedCountry: '',
      selectedState:'',
      selectedCity:''
    };

    this.callApi = this.callApi.bind(this);
    this.handleCountrySelectionChange = this.handleCountrySelectionChange.bind(this);
    this.handleStateSelectionChange = this.handleStateSelectionChange.bind(this);
  }

  handleCountrySelectionChange(value) {
    this.setState({selectedCountry : value});
  }
  handleStateSelectionChange(value) {
    this.setState({selectedCountry : value});
  }
  handleCitySelectionChange(value) {
    this.setState({selectedCountry : value});
  }
  componentDidMount() {
    this.callApi();
  }

  callApi(requestFor, selectedCountry, selectedState) {
    requestFor = requestFor || '';
    if (this.state.countries === '' && requestFor === '' ) {
      fetch('/api/country').then(response => response.json().then(parsedResponse => this.countries = parsedResponse)).catch(err => console.log(err));
    } else if (requestFor === 'state') {
      let url = '/api/' + selectedCountry + '/state';
      fetch(url).then(response => response.json().then(parsedResponse => this.states = parsedResponse)).catch(err => console.log(err));
    } else if (requestFor === 'city') {
      let url = '/api/' + selectedCountry + '/' + selectedState + '/city';
      fetch(url).then(response => response.json().then(parsedResponse => this.cities = parsedResponse )).catch(err => console.log(err));
    }
  };

  render() {
    return (
      <div class="main-container">
        <CountryCombo countryDetails = {this.state.countries} callApi = {this.callApi} handleCountrySelectionChange = {this.handleCountrySelectionChange}/>
        <StateCombo stateDetails = {this.state.states} callApi = {this.callApi}/>
        <CityCombo cityDetails = {this.state.cities}/>
      </div>
    );
  }
}

export default App;
